mapper.update:
update 
	ADMIN_USER au 
set au.password = #{password} 
where au.user_id = #{adminUserId}

     update 
		ADMIN_USER au 
	set au.realname = #{realName} , au.contact=#{contact}
	where au.user_id = #{adminUserId}

mapper.select:
select count(1) from ADMIN_USER au where 1 = 1
and au.username like '%' || #{userName} || '%' ESCAPE '/'
and au.realname like '%' || #{realName} || '%' ESCAPE '/'
and au.contact like '%' || #{contact} || '%' ESCAPE '/'
and au.position like '%' || #{position} || '%' ESCAPE '/'
and au.locked=#{state}
and (au.user_id in ( select pr.user_id from project_r_user pr left join ADMIN_USER a on pr.user_id=a.user_id where pr.project_id=#{projectId} and a.locked='Y') or user_id in (select a.user_id from project p left join ADMIN_USER a on a.user_id=p.manager_id
where p.project_id=#{projectId}
)
or au.locked='N')
