mapper.select:
SELECT id, create_time AS createTime, train_time AS trainTime, project, place, form, result, user_id AS userId, train_end_time as trainEndTime
FROM TRAIN
WHERE id = #{_parameter}

SELECT id, create_time AS createTime, train_time AS trainTime, project, place, form, result, user_id AS userId, train_end_time as trainEndTime
FROM TRAIN
WHERE user_id = #{map.userId}
AND train_time >= #{map.startDate}
AND train_time <= #{map.endDate}
AND project like concat('%',#{map.project,jdbcType=VARCHAR},'%')
ORDER BY train_time DESC
