mapper.update:
UPDATE ATTACHMENT SET

		name = #{name},

	location = #{location},
	type = #{type}

WHERE id = #{id}

mapper.select:
	SELECT id,f_id AS fId,name,location,type,create_Time AS createTime

	FROM ATTACHMENT
 	WHERE id = #{_parameter}

SELECT id,f_id AS fId,name,location,type,create_Time AS createTime,'view' AS editMode

FROM ATTACHMENT

WHERE 1 = 1 


		AND f_id = #{map.fId}



		AND name = #{map.name}



		AND type = #{map.type}



		AND create_Time  >=  #{map.startDate}



		AND create_Time  <=  #{map.endDate}



ORDER BY create_Time DESC
