mapper.select:
SELECT cost_subtype_id AS costSubTypeId,cost_type AS costType,name as name FROM COST_SUBTYPE WHERE 1=1
AND cost_type = #{_parameter}
ORDER BY cost_subtype_id

SELECT name FROM COST_SUBTYPE WHERE 1=1
AND cost_type = #{_parameter}
ORDER BY cost_subtype_id

mapper.insert:
INSERT INTO COST_DEP_DETAIL ( cost_id, cost_dep, cost_year, cost_month, cost_date, cost_num
) VALUES ( #{costId}, #{costDep}, #{costYear}, #{costMonth}, #{costDate}, #{costNum}
)

mapper.update:
UPDATE COST_DEP_DETAIL
SET
	cost_year = #{costYear},
	cost_month = #{costMonth},
	cost_date = #{costDate},
	cost_num = #{costNum}
WHERE cost_id = #{costId}
AND cost_dep = #{costDep}

UPDATE COST_DETAIL SET status = #{status}

	,audit_user = #{auditUser}

,cost_time = now()
WHERE cost_id = #{costId}

UPDATE
	COST_SEQ
SET 
	date = #{date},
	seq = #{seq}
WHERE type = #{type}

mapper.select:
SELECT dep.`name` AS depName, costSub.`name` AS costSubName, costDetail.cost_id AS costId, costDetail.cost_num AS costNum, costDetail.cost_time AS costTime, costDetail.cost_type AS costType, costDetail.cost_subtype_id AS costSubtypeId, costDetail.cost_class as costClass, costDetail.create_time AS createTime, costDetail.remark as remark, costDetail.cost_no as costNo, costDetail.status, code.content as statusName, 'view' as editMode, costDetail.contract_id as contractId, contract.content as contractContent, costDetail.project_id as projectId, project.name as projectName FROM COST_DETAIL costDetail LEFT JOIN COST_SUBTYPE costSub ON costDetail.cost_subtype_id = costSub.cost_subtype_id LEFT JOIN DEPARTMENT dep ON costDetail.dep_id = dep.dep_id LEFT JOIN CODE_CONTENT code on costDetail.status = code.id and codeName = 'CodeStatus' LEFT JOIN CONTRACT contract on costDetail.contract_id = contract.contract_id LEFT JOIN project on costDetail.project_id = project.pj_no where 1=1
AND costDetail.cost_type = #{map.costType}
AND costDetail.cost_subtype_id = #{map.costSubtypeId}
AND costDetail.cost_no like '%${map.costNo}%'
AND costDetail.remark like '%${map.remark}%'
AND costDetail.create_user_id = #{map.userId}
AND costDetail.cost_time >= #{map.startTime}
AND costDetail.cost_time <= #{map.endTime}
ORDER BY costDetail.create_time DESC

SELECT dep.`name` AS depName, costSub.`name` AS costSubName, costDetail.cost_id AS costId, costDetail.cost_num AS costNum, costDetail.cost_time AS costTime, costDetail.cost_type AS costType, costDetail.cost_subtype_id AS costSubtypeId, costDetail.cost_class as costClass, costDetail.create_time AS createTime, costDetail.remark as remark, costDetail.cost_no as costNo, costDetail.status, costDetail.audit_user as auditUser, audit.realname AS auditUserName, costDetail.create_user_id AS subUser, subUser.realname as subUserName, code.content as statusName, 'view' as editMode, costDetail.contract_id as contractId, contract.content as contractContent, costDetail.project_id as projectId, project.name as projectName,
FROM COST_DETAIL costDetail LEFT JOIN COST_SUBTYPE costSub ON costDetail.cost_subtype_id = costSub.cost_subtype_id LEFT JOIN DEPARTMENT dep ON costDetail.dep_id = dep.dep_id LEFT JOIN CODE_CONTENT code on costDetail.status = code.id and codeName = 'CodeStatus' LEFT JOIN ADMIN_USER audit on costDetail.audit_user = audit.user_id LEFT JOIN ADMIN_USER subUser on costDetail.create_user_id = subUser.user_id LEFT JOIN CONTRACT contract on costDetail.contract_id = contract.contract_id LEFT JOIN payment on contract.company = payment.id LEFT JOIN project on costDetail.project_id = project.pj_no WHERE costDetail.status = '05'
AND costDetail.cost_type = #{map.costType}
AND costDetail.cost_subtype_id = #{map.costSubtypeId}
AND costDetail.cost_no like '%${map.costNo}%'
AND costDetail.remark like '%${map.remark}%'
AND DATE_FORMAT(costDetail.cost_time,'%Y-%m') = '${map.searchMonth}'
AND ( costDetail.create_user_id = #{map.userId}
or 1 = 1
or costDetail.dep_id = #{map.depId}
)
AND costDetail.cost_time >= #{map.startTime}
AND costDetail.cost_time <= #{map.endTime}
ORDER BY costDetail.cost_time DESC

SELECT
	cost_type costType
	,sum(cost_num) costNum 
FROM
	COST_DETAIL costDetail
WHERE costDetail.status = '05'


		AND costDetail.cost_type = #{costType}


		AND costDetail.cost_subtype_id = #{costSubtypeId}


		AND costDetail.cost_no like '%${costNo}%'



		AND costDetail.remark like '%${remark}%'



		AND costDetail.cost_time  >=  #{startTime}



		AND costDetail.cost_time  <=  #{endTime}


group BY cost_type

SELECT costSubtype.name,DATE_FORMAT(costDetail.cost_time,'%m') month,sum(costDetail.cost_num) AS total
FROM COST_DETAIL costDetail,COST_SUBTYPE costSubtype  
WHERE costDetail.cost_type = #{costType} 
	  AND costDetail.cost_subtype_id = costSubtype.cost_subtype_id



		AND costDetail.dep_id = #{depId}



	AND costDetail.cost_time  >=  #{startTime}

	AND costDetail.cost_time  <=  #{endTime}

	group by month,costDetail.cost_subtype_id 
	order by month,costDetail.cost_subtype_id

SELECT depart.name,DATE_FORMAT(costDetail.cost_time,'%m') month,sum(costDetail.cost_num) AS total 
FROM COST_DETAIL costDetail, DEPARTMENT as depart
WHERE costDetail.cost_type = #{costType}

	AND costDetail.dep_id = depart.dep_id


		AND costDetail.dep_id = #{depId}


	AND costDetail.cost_time  >=  #{startTime}

	AND costDetail.cost_time  <=  #{endTime}

	group by month,costDetail.dep_id 
	order by depart.name

SELECT sum(costDetail.cost_num) AS total 
FROM COST_DETAIL costDetail
WHERE costDetail.cost_type = #{costType}


		AND costDetail.dep_id = #{depId}


	AND costDetail.cost_time  >=  #{startTime}

	AND costDetail.cost_time  <=  #{endTime}

SELECT depart.name,DATE_FORMAT(costDetail.cost_time,'%m') 'month',costDetail.cost_type AS `type`,SUM(costDetail.cost_num) AS total 
FROM COST_DETAIL costDetail, DEPARTMENT AS depart
WHERE costDetail.dep_id = depart.dep_id

		AND costDetail.dep_id = #{depId}

	AND costDetail.cost_time  >=  #{startTime}
	AND costDetail.cost_time  <=  #{endTime}

	GROUP BY costDetail.cost_type,MONTH,costDetail.dep_id 
	ORDER BY depart.name,MONTH,costDetail.cost_type

SELECT a.cost_dep AS depId, c.name AS depName, sum(a.cost_num) AS costNum, b.cost_type AS costType, a.cost_month AS costMonth FROM COST_DEP_DETAIL a INNER JOIN COST_DETAIL b ON a.cost_id = b.cost_id INNER JOIN DEPARTMENT c ON a.cost_dep = c.dep_id WHERE 1=1
AND a.cost_dep = #{depId}
AND b.cost_type = #{costType} AND a.cost_year = #{curYear} AND a.cost_num > 0 GROUP BY a.cost_dep,a.cost_month,c.name ORDER BY a.cost_dep,a.cost_month
