mapper.select:
select pa.*,p.name as postName,dep.name as depName,cu.realname as createUserName ,au.realname as auditUserName,p.status as postResult
from POSITION_AUDIT pa left join POSITION p on p.post_id = pa.position_id left join DEPARTMENT dep on dep.dep_id = p.dep_id left join ADMIN_USER cu on pa.create_user_id = cu.user_id left join ADMIN_USER au on pa.audit_user_id = au.user_id
where 1=1
and pa.create_user_id = #{map.createUserId,jdbcType=VARCHAR}
and pa.audit_user_id = #{map.auditUserId,jdbcType=VARCHAR}
and pa.result = #{map.result,jdbcType=VARCHAR}
and pa.result is null
order by pa.create_time desc
