mapper.select:
SELECT travel.id as id, travel.create_user AS createUser, travel.start_time AS startTime, dep.`name` AS depName, travel.end_time as endTime, travel.place as place, travel.status as status, travel.remark as remark, 'view' as editMode, au.realname as createUserName, cc.content as statusName FROM travel travel LEFT JOIN DEPARTMENT dep ON travel.dep_id = dep.dep_id LEFT JOIN ADMIN_USER au ON au.user_id = travel.create_user LEFT JOIN CODE_CONTENT cc on cc.id = travel.status where 1=1 AND travel.status = '05'
AND travel.create_user like '%' #{map.name} '%'
AND travel.place like '%' #{map.place} '%'
AND ( travel.create_user = #{map.userId}
or 1 = 1
or travel.dep_id = #{map.depId}
)
And (travel.start_time <= #{map.startTime} and travel.end_time >= #{map.startTime})
And (travel.start_time <= #{map.endTime} and travel.end_time >= #{map.endTime})
order by travel.create_time desc

mapper.update:
UPDATE travel SET status = #{status}

	,audit_user = #{auditUser}

WHERE id = #{id}
